import build from './build'
import strip from '@rollup/plugin-strip'
import { uglify } from 'rollup-plugin-uglify'

export default Object.assign(build, {
  plugins: build.plugins.concat([
    strip({
      functions: ['warn']
    }),
    uglify({
      output: {
        comments: '/^!/'
      },
      compress: {
        drop_console: true
      }
    })
  ])
})
